def ShortenRelation(level, relation,inLaw):
    if len(relation) < 3:
        return ""
    firstPerson=relation[0]
    secondPerson=relation[len(relation)-1]
    if len(relation) == 3:
        return secondPerson+" is "+relation[1]+" of "+firstPerson
    tmpPrefix=""
    tmpSuffix=""
    if level < 0:
        firstPerson=relation[len(relation)-1]
        secondPerson=relation[0]
        level = level*-1
    if level == 0:
        if ("father" in relation[1] or "mother" in relation[1]) and len(relation) == 5:
            tmpPrefix = "sibling"
        elif len(inLaw) > 0 and ("father" in relation[3] or "mother" in relation[3]) and len(relation) == 7:
            tmpPrefix = "sibling"+inLaw
        else:
            tmpPrefix = "cousin"+inLaw
    elif level == 1:
        if len(inLaw) > 0 and len(relation) == 5:
            tmpPrefix = "parent"+inLaw
        else:
            tmpSuffix = "'s parent"
            if ("father" in relation[1] or "mother" in relation[1]) and len(relation) == 7:
                tmpPrefix = "sibling "
            elif len(inLaw) > 0 and ("father" in relation[3] or "mother" in relation[3]) and len(relation) == 9:
                tmpPrefix = "sibling "
                if secondPerson in relation[0]:
                    tmpPrefix = tmpPrefix + inLaw
                else:
                    tmpSuffix = tmpSuffix + inLaw
            else:
                tmpPrefix = "cousin "

    else:
        while level > 2:
            tmpPrefix = tmpPrefix +"great "
            level = level -1
        tmpPrefix = tmpPrefix +"grandparent "+inLaw
    return secondPerson+" is "+tmpPrefix+" of "+firstPerson+tmpSuffix


def bfs(entityOne, entityTwo):
    visited = {}
    prevNode = {}
    visited[entityOne]=True
    q= Queue()
    q.put(entityOne)
    while(q.empty()==False):
        cur=q.get()
        if (cur == entityTwo):
            break
        for s in relationToVisit:
            #print(cur+" "+s)
            if s not in Entity[cur]:
                #print(s+" not in "+cur)
                continue
            for nextNode in Entity[cur][s]:
                if ("father" == s or "mother" == s) and ("adopt" in nextNode.lower() or "legal" in nextNode.lower()):
                    continue
                nextNode = filterChildNode(nextNode)

                if nextNode not in EntityList:
                    for w in EntityList:
                        if w in nextNode:
                            if w in visited:
                                continue
                            if s == "children" and checkAdoptedChild(w, cur):
                                    continue
                            visited[w]=True
                            q.put(w)
                            prevNode[w]=[]
                            prevNode[w].append(cur)
                            prevNode[w].append(s)
                else:
                    if nextNode in visited or (s == "children" and checkAdoptedChild(nextNode, cur)):
                        continue
                    visited[nextNode]=True
                    q.put(nextNode)
                    prevNode[nextNode]=[]
                    prevNode[nextNode].append(cur)
                    prevNode[nextNode].append(s)
    relation = []
    if cur == entityTwo:
        relation.append(cur)
        while(cur != entityOne):
            if "children" in prevNode[cur][1]:
                relation.append("child")
            else:
                relation.append(prevNode[cur][1])
            relation.append(prevNode[cur][0])
            cur = prevNode[cur][0]
        relation.reverse()
    return relation

def SearchGraph(entityOne, entityTwo):
    relation = bfs(entityOne, entityTwo)
    if len(relation) > 0 and "spouse" not in relation[1]:
        relation = bfs(entityTwo, entityOne)
    inLaw = " "
    level = 0
    turn = 1
    while turn < len(relation):
        if turn == 1 and "spouse" in relation[turn] and len(relation) > 3:
            inLaw = " in law "
        if "father" in relation[turn] or "mother" in relation[turn]:
            level = level +1
        elif "child" in relation[turn]:
            level = level -1
        turn = turn +2
    shortRel = ShortenRelation(level, relation,inLaw)

    longRel=""
    turn = 0
    while turn+2 < len(relation):
        longRel = longRel + relation[turn+2]+" is "+relation[turn+1] +" of "+relation[turn]+". "
        turn = turn + 2
    return shortRel,longRel


def expandRelation(rel):
    res=[]
    print(rel)
    if (rel in "parents"):
        res.append(1)
    elif (rel in "children"):
        res.append(-1)
    elif (rel in "spouses"):
        res.append(0)
    elif (rel in "siblings"):
        res.append(1)
        res.append(-1)
    elif (rel in "cousins"):
        res.append(1)
        res.append(1)
        res.append(-1)
        res.append(-1)
    elif ("grandparent" in rel):
        splitter=re.compile(' ')
        tmpstr = splitter.split(rel)
        for w in tmpstr:
            if ("great" in w):
                res.append(1)
            else:
                if "grandparent" in w:
                    res.append(1)
                    res.append(1)
                else:
                    return []                
    return res            


def findRelative(entityOne, entityTwo):
    res=[]
    longRel = expandRelation(entityTwo)
    if len(longRel) == 0:
        return res
    visited = {}
    #prevNode = {}
    visited[entityOne]=True
    q=Queue()
    q.put(entityOne)
    #for i in range(0,len(longRel)):
    for level in longRel:
        #print(level)
        if q.empty()==True  :
            break
        qlen = q.qsize()
        rel = []
        if (level == 1):
            rel.append("father")
            rel.append("mother")
        elif (level == -1):
            rel.append("children")
        else:
            rel.append("spouse")
        while(qlen > 0):
            cur=q.get()
            qlen = qlen - 1
            if cur not in Entity:
                continue
            for w in rel:
                if w not in Entity[cur]:
                    continue
                for nextNode in Entity[cur][w]:
                    if ("father" == w or "mother" == w) and ("adopt" in nextNode.lower() or "legal" in nextNode.lower()):
                        continue
                    nextNode = filterChildNode(nextNode)
                    
                    if nextNode not in EntityList:
                        for wd in EntityList:
                            if wd in nextNode:
                                if wd in visited:
                                    continue
                                if w == "children" and checkAdoptedChild(wd, cur):
                                        continue
                                visited[wd]=True
                                q.put(wd)
                    else:
                        if nextNode in visited or (w == "children" and checkAdoptedChild(nextNode, cur)):
                            continue
                        visited[nextNode]=True
                        q.put(nextNode)
    
    while(q.empty()==False):
        cur=q.get()
        res.append(cur)    
    return res;


# prints relationship between two entities or prints a relative based on type of query.
#example queries
# Query(2, arya stark, mother) // finds mother of arya stark
# Query(1, arya stark, jon snow) // finds relationship between arya stark and jon snow

def Query(queryType, OrigEntityOne, OrigEntityTwo):

    entityOne = OrigEntityOne.lower()
    entityTwo = OrigEntityTwo.lower()
    if entityOne not in EntityList:
        #entityOne = correction(entityOne)
        print("Error: Entity not found in Database")
        return (entityOne + " is not present in Database\n")
    if queryType == '0':
        splitter=re.compile(' ')
        firstName = splitter.split(entityOne)[0]
        res=entityOne
        if "also known as" in Entity[entityOne]:
            res = res + " also known as "+Entity[entityOne]["also known as"][0]+","
        elif "titles" in Entity[entityOne]:
            res = res + " also known as "+Entity[entityOne]["titles"][0]+","
        if "culture" in Entity[entityOne]:
            tmpChar = Entity[entityOne]["culture"][0].lower()[0]
            if tmpChar in ['a', 'e','i','o','u']:
                tmpChar = "an"
            else:
                tmpChar = "a"
            res = res + " is "+tmpChar+" "+Entity[entityOne]["culture"][0]
        if "father" in Entity[entityOne] and "mother" in Entity[entityOne]:
            res = res + ". "+ firstName +" was born to " + filterEntity(Entity[entityOne]["father"][0]) + " and "+ filterEntity(Entity[entityOne]["mother"][0])
        if "allegiance" in Entity[entityOne]:
            res = res +". "+ firstName +" owe's allegiance to "+filterEntity(Entity[entityOne]["allegiance"][0])
        return res
    elif queryType == "1":
        if entityTwo not in EntityList:
            print("Error: Entity not found in Database")
            return (entityTwo + " is not present in Database.")
        shortRel,longRel = SearchGraph(entityOne, entityTwo)
        if shortRel == "":
            return (entityOne + " and " +entityTwo+ " are not related.")
        else:
            return shortRel#+". "+longRel
    elif queryType == "2":
        result = findRelative(entityOne, entityTwo)
        if len(result) == 0:
            print("Error: Information about "+entityTwo+" of "+entityOne + " is not present in database.")
            return ("Information about "+entityTwo+" of "+entityOne + " is not present in database.")
        else:
            res=""
            for w in result:
                if w == result[-1]:
                    res = res+"  and "
                elif res != "":
                    res = res + ", "
                res=res + w
            
            if len(result) == 1:
                plural=" is the "
                if OrigEntityTwo[-1] == 's':
                    OrigEntityTwo = OrigEntityTwo[0:-1]
            else:
                plural=" are the "
                if OrigEntityOne[-1] != 's':
                    OrigEntityTwo = OrigEntityTwo+"s"
            
            #result = OrigEntityOne +"'s" +OrigEntityTwo+ " is "+ filterEntity(Entity[entityOne][entityTwo][0])
            return res + plural + OrigEntityTwo + " of " + OrigEntityOne+"."
    elif queryType == "3":
        if "portrayed by" not in Entity[entityOne]:
            print ("Error: Information about actor who played " + OrigEntityOne + " is not present in database.")
            return ("Information about actor who played "+OrigEntityOne+" is not present in database.")
        else:
            result = ""
            for w in Entity[entityOne]["portrayed by"]:
                if result != "":
                    result = result + ", "
                result = result + w
            return (entityOne+" was portrayed by "+result+".")
        

